<?php
/**
     * 将数组格式化为mongo可用的filter
     * @param array $arr
     * @param string $origin
     * @return array
     */
    private function formatArr2MongoFilter(array $arr, string $origin = 'data'): array
    {
        if (array_keys($arr)) {
            $keys = array_keys($arr);
            foreach ($keys as $key) {
                if (is_array($arr[$key])) {
                    $resName = $origin . '.' . $key;
                    return $this->formatArr2MongoFilter($arr[$key], $resName);
                } else {
                    $resName = $origin . '.' . $key;
                    return ['mongoFilter' => $resName, 'value' => $arr[$key]];
                }
            }
        }
    }
